import React, { useState } from 'react';
import axios from 'axios';

const NewLocationForm2 = () => {
  const [city, setCity] = useState('');
  const [imageUrl, setImageUrl] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();
    const locationData = { city, imageUrl };
    axios.post('http://localhost:9292/locations', { location: locationData })
      .then(response => {
        console.log(response);
        setCity('');
        setImageUrl('');
      })
      .catch(error => console.log(error));
  }

  return (
    <div>
        <h4>Is this list missing a location? Please add details for another Colorado location below:</h4>
        <form onSubmit={handleSubmit}>
            <label htmlFor="city">City: </label>
            <input type="text" id="city" value={city} onChange={e => setCity(e.target.value)}/>
            <br />
            <label htmlFor="image_url" >Image URL: </label>
            <input type="text" id="image_url" alt="image" onChange={e => setImageUrl(e.target.value)}/>
            <br />
            <button type="submit">Add Venue</button>
        </form>
    </div>
  );
}

export default NewLocationForm2;
