import React, {useState, useEffect} from "react";
import Search from "./Search";
import Card from 'react-bootstrap/Card';
import { useParams } from "react-router-dom";

function Location() {
    const { id } = useParams();
    let locationId = Number(id);
    const [location, setLocation] = useState({});

    useEffect(() => {
        fetch(`http://localhost:9292/locations/${id}`)
          .then((r) => r.json())
          .then((location) => setLocation(location)); 
      }, []); 

      console.log(location.venues);

      const venueObj = location.venues.forEach(venue => (
      <li key={venue.id}>
        <h4>{venue.name}</h4>
        <p>{venue.description}</p>
      </li>))

  return (
    <div>
      <h2>{location.city}</h2>
      <p>{location.state}</p>
      <img src={location.image_url} alt={location.city} />
      <h3>Venues</h3>
      <ul>
        {location.venues.forEach(venue => (
          <li key={venue.id}>
            <h4>{venue.name}</h4>
            <p>{venue.description}</p>
          </li>
        ))}
      </ul>
    </div>
  );
}


export default Location;
