import React, {useState, useEffect} from 'react';
import {Switch, Route} from "react-router-dom";
import 'bootstrap/dist/css/bootstrap.min.css';
import NavBar from './NavBar';
import Header from './Header';
import VenueContainer from './VenueContainer';
import LocationContainer from './LocationContainer';
import LocationCard from './LocationCard';
import About from './About';
import NewVenueForm from './NewVenueForm';
import NewLocationForm2 from './NewLocationForm2';
import Home from './Home';

function App() {
  // const [isDarkMode, setIsDarkMode] = useState(false);
  const [data, setData] = useState([]);
  const [locations, setLocations] = useState([]);

  // const handleDarkModeClick = () => {
  //   setIsDarkMode((isDarkMode) => !isDarkMode);
  // }

  useEffect(() => {
    fetch("http://localhost:9292/locations")
      .then((r) => r.json())
      .then((locations) => setLocations(locations)); 
  }, []); 

  return (
    <div 
    // className={"App " + (isDarkMode ? "dark" : "light")}
    >
      <NavBar />
      <Header 
      // isDarkMode={isDarkMode} onDarkModeClick={handleDarkModeClick}
      />
      <br />
        <Switch>
          <Route exact path="/">
            <Home />
          </Route>
          <Route exact path="/locations">
          <LocationContainer locations={locations} setLocations={setLocations}/>
          </Route>
          <Route path="/locations/:id">
            <Location locations={locations} setLocations={setLocations}/>
          </Route>
          <Route path="/venues">
          <VenueContainer data={data} setData={setData}/>
          </Route>
          <Route path="/newvenue">
            <NewVenueForm data={data} setData={setData}/>
          </Route>
          <Route path="/newlocation">
            <NewLocationForm2 locations={locations} setLocations={setLocations}/>
          </Route>
          <Route path="/about">
            <About />
          </Route>
        </Switch>
    </div>
  );
}

export default App;
