import React, {useState, useEffect} from "react";
import Card from 'react-bootstrap/Card';
import Search from "./Search";
import { useParams } from "react-router-dom";

function Location({locations, search, setSearch}) {
    const { id } = useParams();
    const [location, setLocation] = useState({
      venues: []
    });

    useEffect(() => {
      const locationObj = locations.find(l => l.id == id)
      if (locationObj){
        setLocation(locationObj);
      }}, [locations]);



      const venueObj = location.venues.map(venue => (
          <ul skey={venue.id}>
            <Card style={{ width: '50rem' }}>
              <Card.Img variant="top" src={venue.image_url} />
                <Card.Title><h2>{venue.name}</h2></Card.Title>
                <Card.Text>{venue.description}</Card.Text>
            </Card>
          </ul>
        ));

        const filteredVenues = venueObj.filter(venue => {
          return (venue.name.toLowerCase().includes(search.toLowerCase())) 
          || venue.description.toLowerCase().includes(search.toLowerCase())
        })

  return (
    <div>
      <Search onSearch={setSearch}/>
      <br />
      <h2>{location.city}, {location.state}</h2>
      <h3>Venues</h3>
        {venueObj}
    </div>
  );
}


export default Location;
