import React, {useState, useEffect} from "react";
import Card from 'react-bootstrap/Card';
import Search from "./Search";
import { useParams } from "react-router-dom";

function Location({locations}) {
    const { id } = useParams();
    const [location, setLocation] = useState({
      venues: []
    });

    useEffect(() => {
      const locationObj = locations.find(l => l.id == id)
      if (locationObj){
        setLocation(locationObj);
      }
        // const fetchData = async () => {await fetch(`http://localhost:9292/locations/${id}`)
        //   .then((r) => r.json())
        //   .then((location) => setLocation(location));}
        // fetchData();
      }, [locations]); 

      console.log(location);

      const venueObj = location.venues.map(venue => (
          <ul className="card" key={venue.id}>
            <center>
            <Card style={{ width: '75rem' }}>
            <Card.Img variant="top" src={venue.image_url} />
            <Card.Body>
                    <Card.Title><h2>{venue.name}</h2></Card.Title>
                    {/* <Card.Subtitle><b>{state}</b></Card.Subtitle> */}
                    <Card.Text>
                      {venue.description}
                    </Card.Text>
                </Card.Body>
            {/* <h4>{venue.name}</h4>
            <p>{venue.description}</p> */}
            </Card>
            </center>
          </ul>
        ));

  return (
    <div>
      <h2>{location.city}, {location.state}</h2>
      {/* <img src={location.image_url} alt={location.city} /> */}
      <h3>Venues</h3>
        {venueObj}
    </div>
  );
}


export default Location;
