import React from "react";
import Search from "./Search";
import Card from 'react-bootstrap/Card';

function Location({locations, setLocations, search, setSearch}) {

    useEffect(() => {
        fetch("http://localhost:9292/locations")
          .then((r) => r.json())
          .then((locations) => setLocations(locations)); 
      }, []); 



    return (
        <ul className="card">
            <Card  style={{ width: '50rem' }}>
                <Card.Img variant="top" src={location.image_url} />
                <Card.Body>
                    <Card.Title><h2>{location.city}, {location.state}</h2></Card.Title>
                    {/* <Card.Subtitle><b>{state}</b></Card.Subtitle> */}
                    <Card.Text>
                    </Card.Text>
                    {/* <Button variant="primary" onClick={handleDelete}>Delete</Button> */}
                </Card.Body>
            </Card>
            <br/>
            <p></p>
        </ul>
    )
}


export default Location;
