import React, { useState } from 'react';
import axios from 'axios';

const NewLocationForm2 = () => {
  const [city, setCity] = useState('');
  const [imageUrl, setImageUrl] = useState(<img src=""/>);

  const handleSubmit = (event) => {
    event.preventDefault();
    const locationData = { city, imageUrl };
    axios.post('http://localhost:9292/locations', { location: locationData })
      .then(response => {
        console.log(response);
        setCity('');
        setImageUrl('');
      })
      .catch(error => console.log(error));
  }

  return (
    <form onSubmit={handleSubmit}>
      <label htmlFor="city">City:</label>
      <textarea id="city" name="city" value={city} onChange={(event) => setCity(event.target.value)} />

      <label htmlFor="imageUrl">Image URL:</label>
      <input type="text" id="imageUrl" name="imageUrl" value={imageUrl} onChange={(event) => setImageUrl(event.target.value)} />

      <button type="submit">Create Location</button>
    </form>
  );
}

export default NewLocationForm2;
