import React, {useState, useEffect} from "react";
import Search from "./Search";
import LocationCard from "./LocationCard";
//this component is where we store our locations
function LocationContainer({locations, setLocations}) {
  const [search, setSearch] = useState("");
//fetching data from our db.json 
  useEffect(() => {
    fetch("http://localhost:9292/locations")
      .then((r) => r.json())
      .then((locations) => setData(locations)); 
  }, []); 


//handles deleting a venue by taking in its id
      // function handleDeleteLocation(id) {
      //   const updatedLocationArray = data.filter(location => location.id !== id);
      //   setData(updatedLocationArray);
      // }
////new array that filters through if name, description or location matches the search terms. Also handles case insensitive matches
      const filteredLocations = data.filter(location => {
        return (location.city.toLowerCase().includes(search.toLowerCase())) 
        || location.state.toLowerCase().includes(search.toLowerCase())

        // || location.location.toLowerCase().includes(search.toLowerCase())
      })


////iterating over the filtered array and creating a separate LocationCard for each location
      const locationCards = filteredLocations.map(location => {
        return <LocationCard 
          key={location.id} 
          location={location} 
          />
      })


    return (
        <div>
          <Search onSearch={setSearch}/>
          <br />
          <h1>Locations:</h1>
            <ul className="cards">
                {locationCards}
            </ul>
        </div>
    )
}

export default LocationContainer;
