import React from "react";
import { useState, useEffect } from "react";
import Card from 'react-bootstrap/Card';
import VenueCard from "./VenueCard";

//this component is for each individual location
function LocationCard({location, data, setData}) {
    const [search, setSearch] = useState("");
    // const [venues, setVenues] = useState("");
    //destructuring the location object
    const {city, state, image_url} = location;

    useEffect(() => {
        fetch("http://localhost:9292/venues")
          .then((r) => r.json())
          .then((data) => setData(data)); 
      }, []); 

    //   function handleDeleteVenue(id) {
    //     const updatedVenueArray = data.filter(venue => venue.id !== id);
    //     setData(updatedVenueArray);
    //   }

      ////new array that filters through if name, description or location matches the search terms. Also handles case insensitive matches
    //   const filteredVenues = venues.filter(venue => {
    //     return (venue.name.toLowerCase().includes(search.toLowerCase())) 
    //     || venue.description.toLowerCase().includes(search.toLowerCase())
    //     // || venue.location.toLowerCase().includes(search.toLowerCase())
    //   })
////iterating over the filtered array and creating a separate VenueCard for each venue
    //   const venueCards = filteredVenues.map(venueObj => {
    //     return <VenueCard 
    //       key={venueObj.id} 
    //       venue={venueObj} 
    //     //   onDeleteVenue={handleDeleteVenue}
    //       />
    //   })


    return (
        <ul className="card">
            <Card style={{ width: '50rem' }}>
            <Card.Link href="http://localhost:9292/venues">
                <Card.Img variant="top" src={image_url} />
                <Card.Body>
                    <Card.Title><h2>{city}, {state}</h2></Card.Title>
                    {/* <Card.Subtitle><b>{state}</b></Card.Subtitle> */}
                    <Card.Text>
                    </Card.Text>
                    {/* <Button variant="primary" onClick={handleDelete}>Delete</Button> */}
                </Card.Body>
                </Card.Link>
            </Card>
            <br/>
            <p></p>
        </ul>
    )
}

export default LocationCard;
